async function petAddForm(d){d.preventDefault();const a=document.querySelector("#added-pet").value,b=await fetch("/api/pet",{method:"POST",body:JSON.stringify({pet_name:a}),headers:{"Content-Type":"application/json"}});b.ok?document.location.replace("/profile"):alert("Failed to add pet")}const delButtonHandler=async c=>{const d=c.target.closest(".remove-btn");if(d){const b=d.getAttribute("data-id"),a=await fetch(`/api/pet/${b}`,{method:"DELETE"});a.ok?document.location.replace("/profile"):alert("Failed to delete pet")}};document.querySelector("#petAdd").addEventListener("click",petAddForm),document.querySelector(".pet-list").addEventListener("click",delButtonHandler);